void renderScene(RenderTexture2D targetScene, float scale)
{
    BeginDrawing();
        ClearBackground(BLACK);

        Rectangle sourceRect = 
        { 
            0.0f, 
            0.0f,
            static_cast<float>(targetScene.texture.width), 
            -static_cast<float>(targetScene.texture.height) 
        };

        Rectangle destRect = {
            (GetScreenWidth() - (smallWidth * scale)) * 0.5f,
            (GetScreenHeight() - (smallHeight * scale)) * 0.5f,
            smallWidth * scale,
            smallHeight * scale
        };

        DrawTexturePro(
            targetScene.texture,
            sourceRect,
            destRect,
            Vector2{ 0, 0 },
            0.0f,
            WHITE
        );

        // Debugging to test FPS
        //DrawFPS(GetScreenWidth() - 95, 10);
    EndDrawing();
}

void Init() {
    monitor.monitor_id = GetCurrentMonitor();
    monitor.monitor_width = GetMonitorWidth(monitor.monitor_id);
    monitor.monitor_height = GetMonitorHeight(monitor.monitor_id);

    // player initial things
    {
        player.pos = (Vector2){3.5f, 2.5f};
        player.lastValid = player.pos;
        player.x = 3;
        player.y = 2;
        player.velocity = (Vector2){0.0f, 0.0f};
        player.velocityMin = (Vector2){-25.0f, -std::numeric_limits<float>::max()};
        player.velocityMax = (Vector2){25.0f, std::numeric_limits<float>::max()};
        player.grounded = true;
        player.speed = 5;
    }

    // window
    window.avgSize = (Vector2) {(float)screenWidth, (float)screenHeight};
    window.minSize = (Vector2) {(float)(50),(float)(50)};
    window.maxSize = (Vector2) {(float)(screenWidth+10),(float)(screenHeight+10)};

    SetConfigFlags(FLAG_WINDOW_UNDECORATED);
    InitWindow(screenWidth, screenHeight, "Game");

    SetTargetFPS(120);
}

int main(void)
{
    Init();

    RenderTexture2D targetScene = LoadRenderTexture(smallWidth, smallHeight);
    SetTextureFilter(targetScene.texture, TEXTURE_FILTER_POINT);

    // Load Background Texture
    Image bg = LoadImage("resources/sprites/collision_mask.png");
    Texture2D texture = LoadTextureFromImage(bg);
    UnloadImage(bg);

    // Main game loop
    while (!WindowShouldClose())
    {
        // Resets player pos to start
        if (IsKeyPressed(KEY_E)) 
        {
            player.pos = (Vector2){3.5f, 2.5f};
        }

        MovePlayer(&player, map);
        LerpSizeOnVelocity(&player, &window, &monitor);
        
        BeginTextureMode(targetScene);

            ClearBackground(RAYWHITE);
            
            DrawTextureV(texture, (Vector2) { (float)(smallWidth/2-player.x), (float)(smallHeight/2-player.y)}, WHITE);
            DrawRectangle(smallWidth/2, smallHeight/2, 1, 1, SKYBLUE);
            
        EndTextureMode();

        // call audio here

        renderScene(targetScene, scale);
    }
    
    CloseWindow();

    return 0;
}